import React, { Component } from "react";
import Tiles from "../ui-design/Tiles";
import Slider from "react-slick";
import PropTypes from "prop-types";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Home.css';
import { useRef } from 'react';
import gsap from 'gsap';
// import { useGSAP } from '@gsap/react';
import { Tilt } from 'react-tilt';

// gsap.registerPlugin(useGSAP);

class Home extends Component {
    render(){
        let setanimateload = false;


        // Similar to componentDidMount and componentDidUpdate:
        // useEffect(() => {
        //     // Update the document title using the browser API
        //     setTimeout({

        //     },2000);
        // });

        // gsap.to(".mohit-text", { rotate: 360, duration: 5 })

        const defaultOptions = {
            reverse: false,  // reverse the tilt direction
            max: 25,     // max tilt rotation (degrees)
            perspective: 1000,   // Transform perspective, the lower the more extreme the tilt gets.
            scale: 1,    // 2 = 200%, 1.5 = 150%, etc..
            speed: 300,   // Speed of the enter/exit transition
            transition: true,   // Set a transition on enter/exit.
            axis: null,   // What axis should be disabled. Can be X or Y.
            reset: true,    // If the tilt effect has to be reset on exit.
            easing: "cubic-bezier(.03,.98,.52,.99)",    // Easing on enter/exit.
        };

        let slickNonAutoSliderSettings = {
            dots: false,
            infinite: false,
            slidesToShow: 3,
            slidesToScroll: 1,
            autoplay: false,
            cssEase: "linear",
            variableWidth: false
        };

        let slickSliderSettings = {
            dots: false,
            infinite: true,
            slidesToShow: 3,
            slidesToScroll: 1,
            autoplay: true,
            speed: 30000,
            autoplaySpeed: 0.0001,
            cssEase: "linear",
            variableWidth: false
        };

        return (
            <div className="Home">

                <section id="logo-animate-section">

                </section>

                <section id="short-desc-section">
                    <div className="text-wrapper">
                        <div className={"mohit-text "+(setanimateload ? 'show':'hide')}>
                            <span>M</span>
                            <span>O</span>
                            <span>H</span>
                            <span>I</span>
                            <span>T</span>
                        </div>

                        <div className={"satoskar-text "+(setanimateload ? 'show':'hide')}>
                            <span>S</span>
                            <span>A</span>
                            <span>T</span>
                            <span>O</span>
                            <span>S</span>
                            <span>K</span>
                            <span>A</span>
                            <span>R</span>
                        </div>

                        <figure className="mohit-figure backgroundImage">
                            <img src="./src/Mohit-04.jpg" alt="mohit" className="w-100"/>
                        </figure>
                    </div>

                </section>

                <section id="project-scroll-section">
                    {/* <Slider className="slider-container" {...slickNonAutoSliderSettings}>
                        <div>
                            <Tilt options={defaultOptions}><Tiles></Tiles></Tilt>
                        </div>
                        <div>
                            <Tiles></Tiles>
                        </div>
                        <div>
                            <Tiles></Tiles>
                        </div>
                        <div>
                            <Tiles></Tiles>
                        </div>
                        <div>
                            <Tiles></Tiles>
                        </div>
                        <div>
                            <Tiles></Tiles>
                        </div>
                        <div>
                            <Tiles></Tiles>
                        </div>
                    </Slider> */}

                    {/* <Slider className="slider-container" {...slickSliderSettings}>
                        <div>
                            <Tiles></Tiles>
                        </div>
                        <div>
                            <Tiles></Tiles>
                        </div>
                        <div>
                            <Tiles></Tiles>
                        </div>
                        <div>
                            <Tiles></Tiles>
                        </div>
                    </Slider> */}
                </section>



            </div>
        );
    }

}

export default Home;
